MapEnts = {}

function GM:RegisterMapEnt(map, ent, pos, ang, color, model, drawstatus)
	table.insert(MapEnts, {map = map, ent = ent, pos = pos, ang = ang, color = color, model = model, drawstatus = drawstatus})
end

